pragma solidity 0.4.21;

import './PickToken.sol';
import './Crowdsale.sol';
import './SafeMath.sol';

/*
 * Allocation is used for pre and post allocations of tokens, these are usually for things such as noting down buyers in 
 * a private pre-sale and for allocating percentages of tokens to different things such as bounty/founders etc.
 *
 * NOTE: We need to think about how this affects the hard/soft cap (do we count it or not count the tokens we've allocated)?
*/
contract Allocation is Crowdsale {

  using SafeMath for uint256;

  bool preallocated = false;
  bool postallocated = false;

  mapping(address => uint256) public presaleBalanceOf;
  mapping(address => uint256) public presaleTokenBalanceOf;
  mapping(address => uint256) public postsalePercentages;
  mapping(address => uint256) public postsaleLock;

  uint256 public presaleWeiRaised;
  uint256 public presaleTokensSent;

  event PreAllocation(address indexed beneficiary, uint256 tokens, uint256 price);
  event PostAllocation(address indexed beneficiary, uint percentage, uint256 lockedUntil);
  event PostAllocationCollected(address indexed beneficiary, uint tokens, uint time);

  modifier untilSet(bool _check) {
    if(_check) {
      revert();
    }
    _;
  }

  function Allocation(string _name, bool _capped, bool _capInETH, uint256 _saleId, bool _allocation, uint256 _startTime, uint256 _endTime, uint256 _rate, address _wallet, address _beneficiary, PickToken _token)
    Crowdsale(_name, _capped, _capInETH, _saleId, _allocation, _startTime, _endTime, _rate, _wallet, _beneficiary, _token)
     public {}

  function setAllocations() public onlyOwner {
    preallocated = true;
  }

  /**
   * Preallocate tokens for the early investors.
   * This will be a fixed amount of tokens i.e. for pre-sale buyers
   * This will NOT be included in tokensSent for the public sale
   */
  function preallocate(address receiver, uint tokens, uint weiPrice, bool included) public onlyOwner untilSet(preallocated) {
    uint decimals = token.decimals();
    uint tokenAmount = tokens * 10 ** decimals;
    uint weiAmount = weiPrice * tokens; 

    validPreallocation(tokenAmount, included);

    // Add presale data about tokens sent
    presaleWeiRaised = presaleWeiRaised.add(weiAmount);
    presaleTokensSent = presaleTokensSent.add(tokenAmount);

    presaleTokenBalanceOf[receiver] = presaleTokenBalanceOf[receiver].add(tokenAmount);
    presaleBalanceOf[receiver] = presaleBalanceOf[receiver].add(weiAmount);

    produceTokens(receiver, weiAmount, tokenAmount);
    PreAllocation(receiver, tokens, weiPrice);
  }

  // This is here to be overwritten by capped logic if necessary
  function validPreallocation(uint tokenAmount, bool included) internal {
    // Sale must not have started
    require(now < startTime);
  }

  /*
   * Allocate a % of tokens to various peers/teams.
   * This will occur after the sale has finished (as need to know how many tokens sold)
   * There is also an option to lock these tokens for a certain period of time
   * I.e. 5% of tokens goes to the founders, but they are locked for 12 months
   */
   // May have to store inclusion/exclusion
  function postallocate(address receiver, uint toAllocate, uint256 lockedUntil) public onlyOwner untilSet(postallocated) {
    // Sale must not have started
    require(now < startTime);

    postsalePercentages[receiver] = postsalePercentages[receiver] + toAllocate;
    postsaleLock[receiver] = lockedUntil;

    PostAllocation(receiver, toAllocate, lockedUntil);
  }

  /*
   * Allowing someone to claim their post allocation
   * The funds must not be locked and the sale must have ended.  
   */
  function claimPostAllocation() external {
    // Sale must have ended, tokens must not be locked and the sender must actually have something to withdraw
    require(hasEnded());
    require(now > postsaleLock[msg.sender]);
    require(postsalePercentages[msg.sender] != 0);

    uint toSend = getTokensToAllocate();

    postsalePercentages[msg.sender] = 0;

    tokensSent = tokensSent.add(toSend);
    tokenBalanceOf[msg.sender] = tokenBalanceOf[msg.sender].add(toSend);

    produceTokens(msg.sender, rate, toSend);
    PostAllocationCollected(msg.sender, toSend, now);
  }

  /*
   * Get amount of tokens to allocate to call the calculate Percent function
   * This can be overwritten for appropriate cap logic
  */
  function getTokensToAllocate() internal view returns (uint) {
    uint256 toSend;

    uint256 percent = postsalePercentages[msg.sender];
    toSend = calculateInclusionPercentage(tokensSent, percent);

    return toSend;
  }

  /*
   * Calculate the % of funds for teams/peers in open ended sale
   * This is used in open sales where the owner would like the teams to then own X% of the total supply of tokens after the sale is finished
   * I.e. we have 1000 tokens and we sell 500 tokens and would like to allocate 10% of total supply to the team
   * The owner would not want to simply allocate 50 tokens as shown above, instead he must do the following:
   * 1. Take total sold and divide by 90 (to find 1%) which in our example is 5.5556
   * 2. Multiply the 1% by 10 to get the 10% of tokens that should be allocated which is 55.55556
   * -- This is then 555.5556 tokens allocated with the team owning 55.5556 which equates to 10% of total supply
   */
  function calculateInclusionPercentage(uint _tokensSold, uint _percent) public view returns (uint) {
    // Using the example above:

    // Use for decimals
    uint precision = 10 ** 10;

    // 100 - 10% = 90
    uint divisor = 100 - _percent;
    // tokens sold = 500 multiplied for precision
    uint tokens = _tokensSold.mul(precision);
    // 1% = 90% / 90 so 500/90 (or 1%) = 5.5556 (with precision this is actually 5555556)
    uint one_percent = tokens.div(divisor);
    // 1% * 10 = 10% so 5.5556 * 10 = 55.5556 (with precision this is actually 5555556)
    uint toSend = one_percent.mul(_percent);
    // Take off precision to leave us with the closest number possible (5555556 -> 55.55556 or 55 tokens)
    uint answer = toSend.div(precision);

    return answer;
  }

}
