pragma solidity 0.4.21;

/*
 * This token is part of Pickeringware ltds smart contracts
 * It is used to specify certain details about the token upon release
 */

contract IconemySale {
  string public name;
  bool public capped;
  bool public capInETH;
  bool public allocation;
  uint256 public saleId;
  uint256 public reservedTokens = 0;

  // Changed implentation to include soft/hard caps
  function IconemySale(string _name, bool _capped, bool _capInETH, uint256 _saleId, bool _allocation) public {
    name = _name;
    capped = _capped;
    capInETH = _capInETH;
    saleId = _saleId;
    allocation = _allocation;
  }
}
